sys_script.script:
var demand = new GlideRecord('dmn_demand');
demand.addQuery('pm_project', current.sys_id);
//demand.addQuery('state', '8');
demand.query();

gs.addInfoMessage('>>>> project ' + demand.project);
//gs.addInfoMessage('>>>> project ' + demand.project.getDisplayValue());

if (demand.next()) {
	var project = new GlideRecord(pm_project);
	project.addQuery('project.sys_id', demand.project);
	project.query();
	gs.addInfoMessage('>>>> project ' + project.name.getDisplayValue());

	if (project.next()){
		project.setValue('x_snc_bvm_demand', demand.getValue('sys_id'));
		project.setValue('x_snc_bvm_business_drivers', demand.getValue('x_snc_bvm_business_drivers'));
		project.setValue('x_snc_bvm_requesting_business_unit', demand.getValue("x_snc_bvm_requesting_business_unit"));

		gs.addInfoMessage('x_snc_bvm_business_drivers: ' + current.x_snc_bvm_business_drivers);

		project.setValue('x_snc_bvm_risk_of_performing', demand.getValue.x_snc_bvm_risk_of_performing);
		project.setValue('x_snc_bvm_risk_of_not_performing', demand.getValue.x_snc_bvm_risk_of_not_performing);
		project.setValue('x_snc_bvm_initiating_sponsor', demand.getValue.x_snc_bvm_initiating_sponsor);
		project.setValue('x_snc_bvm_sustaining_sponsor', demand.getValue.x_snc_bvm_sustaining_sponsor);
		project.setValue('x_snc_bvm_enablers', demand.getValue.x_snc_bvm_enablers);
		project.setValue('x_snc_bvm_barriers', demand.getValue.x_snc_bvm_barries);
		project.setValue('x_snc_bvm_in_scope', demand.getValue.x_snc_bvm_in_scope);
		project.setValue('x_snc_bvm_out_of_scope', demand.getValue.x_snc_bvm_out_of_scope);
		project.setValue('x_snc_bvm_assumptions', demand.getValue.x_snc_bvm_assumptions);
		project.setValue('x_snc_bvm_constraints', demand.getValue.x_snc_bvm_constraints);
		project.update();
		gs.addInfoMessage('x_snc_bvm_business_drivers: ' + project.x_snc_bvm_business_drivers);
		gs.addInfoMessage('x_snc_bvm_risk_of_not_performing', current.x_snc_bvm_risk_of_not_performing);
	}

	else {
		gs.info('>>>> no record found ');

	}
}
