//(function executeRule(current, previous /*null when async*/) {

	var demand = new GlideRecord('dmn_demand');
	if(GlidePluginManager.isActive('com.snc.project_management_v3')){
		demandTable = SNC.PPMConfig.getDemandTable(current.getTableName());
	}

	demand.setValue("business_case", current.business_case);
	demand.setValue("short_description", current.short_description);
	demand.setValue("category","strategic");
	demand.setValue("type", "project");
	demand.setValue("submitter", current.submitter);
	demand.setValue("parent", current.sys_id);
	demand.setValue("idea", current.sys_id);
	demand.setValue("x_snc_bvm_on_behalf_of", current.x_snc_bvm_on_behalf_of);
	demand.setValue("x_snc_bvm_requesting_business_unit", current.x_snc_bvm_business_unit);
	demand.setValue("x_snc_bvm_business_drivers", current.x_snc_bvm_business_drivers);
	demand.setValue("x_snc_bvm_risk_of_performing", current.risk_of_performing);
	demand.setValue("x_snc_bvm_risk_of_not_performing", current.risk_of_not_performing);
	demand.setValue("x_snc_bvm_initiating_sponsor", current.x_snc_bvm_initiating_sponsor);
	demand.setValue("x_snc_bvm_sustaining_sponsor", current.x_snc_bvm_sustaining_sponsor);
	demand.setValue("x_snc_bvm_enablers", current.x_snc_bvm_enablers);
	demand.setValue("x_snc_bvm_barriers", current.x_snc_bvm_barries);
	demand.setValue("x_snc_bvm_in_scope", current.x_snc_bvm_in_scope);
	demand.setValue("x_snc_bvm_out_of_scope", current.x_snc_bvm_out_of_scope);
	demand.setValue("x_snc_bvm_assumptions", current.x_snc_bvm_assumptions);
	demand.setValue("x_snc_bvm_constraints", current.x_snc_bvm_constraints);

	gs.log("Constraints: " + current.x_snc_bvm_constraints);
	gs.log("Business Drivers: " + current.x_snc_bvm_business_drivers);

	var dmnId = demand.insert();
	demand.get(dmnId);
	current.demand = dmnId;
	current.stage = 'demand';
	var s = 'Demand <a href ="/dmn_demand.do?sysparm_query=number%3D' + demand.getValue('number') + '">'+ demand.getValue('number') +'</a>'+' has been created.<br/>';

	gs.addInfoMessage(s);
//})(current, previous);
